import { FlatList, StyleSheet, Text, View } from "react-native";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { SafeAreaView } from "react-native-safe-area-context";
import { TouchableOpacity } from "react-native-gesture-handler";
import { removeProduct } from "../../store/cart/slice";
import { Feather } from "@expo/vector-icons";

const CartScreen = () => {
  const dispatch = useDispatch();
  const cartState = useSelector((state) => state.cart.items);

  const cartItem = ({ item }) => {
    return (
      <View style={styles.cartContainer}>
        <Text style={styles.quantity}>{item.qty}X</Text>
        <Text style={styles.title}>{item.name}</Text>
        <Text style={styles.price}>â‚¬{item.price*item.qty}</Text>
        <TouchableOpacity
          style={styles.remove}
          onPress={() => dispatch(removeProduct(item))}
        >
          <Feather size={24} color="red" name="delete" />
        </TouchableOpacity>
      </View>
    );
  };

  return (
    <SafeAreaView>
      <FlatList
        data={cartState}
        keyExtractor={(item) => item.name}
        renderItem={cartItem}
        ListEmptyComponent={() => (
          <Text style={styles.emptyCart}>Geen producten aanwezig</Text>
        )}
      />
    </SafeAreaView>
  );
};

export default CartScreen;

const styles = StyleSheet.create({
  cartContainer: {
    flex:1,
    flexDirection: "row",
    margin: 10,
    justifyContent: "space-between",
  },
  quantity: {
    fontSize: 20,
    padding: 5,
  },
  title: {
    fontSize: 20,
    fontWeight: 1000,
    padding: 5,
  },
  price: {
    fontSize: 20,
    textAlign: "right",
    padding: 5,
  },
  remove: {
    padding: 5,
  },
  emptyCart: {
    justifyContent: "center",
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 22,
    textAlignVertical: cent
  }
});
