import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    total: 0,
    quantity: 0,
  },
  reducers: {
    addProduct: (state, action) => {
      const itemIndex = state.items.findIndex(
        (cartItem) => cartItem.name === action.payload.name
      );
      if (itemIndex >= 0) {
        state.items[itemIndex].qty += 1;
      } else {
        const tempProduct = { ...action.payload, qty: 1 };
        state.items.push(tempProduct);
      }
    },
    removeProduct: (state, action) => {
      state.items.map((cartItem) => {
        if (cartItem.name === action.payload.name) {
          const nextCartItems = state.items.filter(
            (item) => item.name !== cartItem.name
          );
          state.items = nextCartItems;
        }
      });
      return state;
    },
    getTotalPrice: (state, action) => {
      let { total, quantity } = state.items.reduce((cartTotal, cartItem) => {
        const { price, qty } = cartItem;
        const itemTotal = price * qty;

        cartTotal.total += itemTotal;
        cartTotal.quantity += qty;

        return cartTotal;
      });
      state.quantity = quantity;
      state.total = total;
    },
  },
});

export const { actions, reducer } = cartSlice;
export const { addProduct, removeProduct } = actions;
